export default {
	myVar1: [],
	myVar2: {},
	formatUserSites: () => {
		let ret = []
		if (GetUserSites.data!= null){
			if(GetUserSites.data.data.getUser.hasRole.length > 0){
				for(let i = 0; i < GetUserSites.data.data.getUser.hasRole.length; i++){
					for(let j = 0; j < GetUserSites.data.data.getUser.hasRole[i].responsibilities.length; j++){
						let line = {
							"value": GetUserSites.data.data.getUser.hasRole[i].responsibilities[j].id,
							"label": GetUserSites.data.data.getUser.hasRole[i].responsibilities[j].name
						}
						ret.push(line)
					}
				}
			}
		}

		return ret
	},
	formatJobOrderTable: ()=>{
  let ret = [];
  if (QueryJobOrders.data.data.queryJobOrdersWithFilter.length > 0) {
            for (let j = 0; j < QueryJobOrders.data.data.queryJobOrdersWithFilter.length; j++) {
              let input = QueryJobOrders.data.data.queryJobOrdersWithFilter[j];
              let materialCode = "";
              let operationsRequestCode = "";
              operationsRequestCode = input.operationsRequest.code;
              if (input.operationsRequest.operationsDefinition != null) {
                if (input.operationsRequest.operationsDefinition.material != null) {
                  materialCode = input.operationsRequest.operationsDefinition.material.code;
                }
              }
              let line = {
                id: input.id,
                name: input.name,
                dispatchStatus: input.dispatchStatus,
                materialCode: materialCode,
                operationsRequestCode: operationsRequestCode,
                JobOrderQuantity: input.quantity,
                OperationsRequestQuantity: input.operationsRequest.quantity,
                ActualProducedQuantity: input.operationsRequest.actualProducedQuantity,
              };
              ret.push(line);
    }
  }

  return ret;
},
	formatPersonnelOutput: ()=>{
		let ret = []
		if(JobResponseList.selectedItem!=null){
			if(JobResponseList.selectedItem.personnelActual.length > 0){
				for(let i = 0; i < JobResponseList.selectedItem.personnelActual.length; i++){
					let personnelActual = JobResponseList.selectedItem.personnelActual[i]
					let uom = ""
					if(personnelActual.quantityUoM != null){
						uom = personnelActual.quantityUoM.code
					}
					let line = {
						"name": personnelActual.person.name,
						"quantity":personnelActual.quantity,
						"quantityUoM":uom
					}
					ret.push(line)
				}
			}
		}
		return ret
	},
	formatMaterialActualOutput: ()=>{
		let ret = []
		if(JobResponseList.selectedItem!=null){
			if(JobResponseList.selectedItem.materialActual.length > 0){
				for(let i = 0; i < JobResponseList.selectedItem.materialActual.length; i++){
					let materialActual = JobResponseList.selectedItem.materialActual[i]
					let uom = ""
					if(materialActual.quantityUoM != null){
						uom = materialActual.quantityUoM.code
					}
					let subLot = ""
					if(materialActual.materialSubLot != null){
						subLot = materialActual.materialSubLot.code
					}
					let line = {
						"name": materialActual.materialDefinition.code,
						"quantity":materialActual.quantity,
						"quantityUoM":uom,
						"materialUse":materialActual.materialUse,
						"materialSubLot":subLot
					}
					ret.push(line)
				}
			}
		}
		return ret
	},
	formatEquipmentActualOutput: ()=>{
		let ret = []
		if(JobResponseList.selectedItem!=null){
			if(JobResponseList.selectedItem.equipmentActual.length > 0){
				for(let i = 0; i < JobResponseList.selectedItem.equipmentActual.length; i++){
					let equipmentActual = JobResponseList.selectedItem.equipmentActual[i]
					let uom = ""
					if(equipmentActual.quantityUoM != null){
						uom = equipmentActual.quantityUoM.code
					}
					let line = {
						"name": equipmentActual.equipment.name,
						"quantity":equipmentActual.quantity,
						"quantityUoM":uom,
						"materialUse":equipmentActual.materialUse
					}
					ret.push(line)
				}
			}
		}
		return ret
	},
	formatJobOrderFilter:()=>{
		let equipmentField = []
		if (!_.isEmpty(WarehouseSelect.selectedOptionValues)){
				for(let i = 0; i < WarehouseSelect.selectedOptionValues.length; i++){
					let line = {
						"id": WarehouseSelect.selectedOptionValues[i]
					}
					equipmentField.push(line)
				}
		}
		let statusField = []
		if (!_.isEmpty(SelectStatus.selectedOptionValues)){
			for(let i = 0; i < SelectStatus.selectedOptionValues.length; i++){
				statusField.push(SelectStatus.selectedOptionValues[i])
			}
		}
		let output = {
			"equipment": equipmentField,
			"status": statusField
		}
		return output
	}
}
