actionConfiguration.body:
()=>{
		let equipmentField = []
		if (!_.isEmpty(EquipmentTreeSelect.selectedOptionValues)){
				for(let i = 0; i < EquipmentTreeSelect.selectedOptionValues.length; i++){
					let line = {
						"id": EquipmentTreeSelect.selectedOptionValues[i]
					}
					equipmentField.push(line)
				}
		}
		let statusField = []
		if (!_.isEmpty(SelectStatus.selectedOptionValues)){
			for(let i = 0; i < SelectStatus.selectedOptionValues.length; i++){
				statusField.push(SelectStatus.selectedOptionValues[i])
			}
		}
		let personField = []
		if (!_.isEmpty(PersonSelect.selectedOptionValues)){
			for(let i = 0; i < PersonSelect.selectedOptionValues.length; i++){
				let line = {
					"id": PersonSelect.selectedOptionValues[i]
				}
				personField.push(line)
			}
		}
		let operationsDefinitionField = []
		if (!_.isEmpty(OperationsDefinitionSelect.selectedOptionValue)){
			let line = {
				"id": OperationsDefinitionSelect.selectedOptionValue
			}
			operationsDefinitionField.push(line)
		}
		let operationsSegmentField = []
		if (!_.isEmpty(OperationsSegmentSelect.selectedOptionValues)){
			for(let i = 0; i < OperationsSegmentSelect.selectedOptionValues.length; i++){
				let line = {
					"id": OperationsSegmentSelect.selectedOptionValues[i]
				}
				operationsSegmentField.push(line)
			}
		}
		let from = null
		if (FromDateTimePicker.formattedDate != ""){
			from = FromDateTimePicker.formattedDate
		}
		let to = null
		if (ToDateTimePicker.formattedDate != ""){
			to = ToDateTimePicker.formattedDate
		}
		let output = {
			"equipment": equipmentField,
			"status": statusField,
			"person":personField,
			"operationsDefinition":operationsDefinitionField,
			"operationsSegment":operationsSegmentField,
			"from":from,
			"to":to
		}
		return output
	}

