actionConfiguration.body:
{{
JSON.stringify({
    variables: {
  "input": {
    "personnelActual": formatPersonsSelect.combineTwoLists(),
    "jobOrder": {
      "id": QueryJobOrderDetails.data.data.getJobOrder.id
    },
    "createdDateTime": moment(),
		"startDateTIme":moment,
    "operator": {
      "username": appsmith.user.email
    }
  },
  "jobOrderID": [
    QueryJobOrderDetails.data.data.getJobOrder.id
  ]
},
    query: `
mutation($input:[AddJobResponseInput!]!,$jobOrderID:[ID!]){
  addJobResponse(input:$input){
    jobResponse{
      id
    }
  }
  updateJobOrder(input:{filter:{id:$jobOrderID},set:{dispatchStatus:ACTIVE}}){
    jobOrder{
      id
    }
  }
}
    `
})
}}

