actionConfiguration.body:
{{
JSON.stringify({
    variables: {

  "input": [
    {
      "name": InputOrderNumber.text,
    	"createdDateTime": moment(),
      "equipment": {
        "id": SelectCreateOrderWarehouse.selectedOptionValue
      },
      "operationsSegment": {
        "id": SelectCreateOrderOperationsSeg.selectedOptionValue
      },
      "operationsRequest": {
      	"id": GetOperationsRequest.data.data.queryOperationsRequest[0].id
      },
      "dispatchStatus": "CREATED",
      "isActive": true,
      "jobResponses": {
        "isActive": true,
        "startDateTime": moment(),
        "equipment": {
          "id": SelectCreateOrderWarehouse.selectedOptionValue
        }
      }
    }
  ]

		},
    query: `
mutation AddJobOrder($input:[AddJobOrderInput!]!){
  addJobOrder (input:$input) {
    numUids
  }
}
    `
})
}}

