actionConfiguration.body:
() => {
		if (tabsMaterialType.selectedTab == "Produce Serialised"){
			let ret = {
				"material": {
					"code": GetMaterialSpecification.data.data.getMaterialSpecification.material.code
				},
				"subLot": {
					"code": InputUnitDetailsBarcode.text
				},
				"status": {
					"code": GetMaterialSpecification.data.data.getMaterialSpecification.allowedStates[0].code
				},
				"carrier": {
					"code": inpSerialPalletLabel.text
				},
				"materialUse": GetMaterialSpecification.data.data.getMaterialSpecification.materialUse,
				"operationsSegment": {
					"id": QueryJobOrderDetails.data.data.getJobOrder.operationsSegment.id
				},
				"jobResponse": {
					"id": QueryJobOrderDetails.data.data.getJobOrder.jobResponses[0].id
				},
				"owner": {
					"id": selectOwnerProdSerialised.selectedOptionValue
				},
				"quantity": 1.0
			}
			return ret
		}
		if (tabsMaterialType.selectedTab == "Produce Non-serialised"){
			if (tabsProducePalletBinSelect.selectedTab == "Pallet") {
				let ret = {
				"material": {
					"code": GetMaterialSpecification.data.data.getMaterialSpecification.material.code
				},
				"status": {
					"code": GetMaterialSpecification.data.data.getMaterialSpecification.allowedStates[0].code
				},
				"carrier": {
					"code": inpProducePalletLabel.text
				},
				"materialUse": GetMaterialSpecification.data.data.getMaterialSpecification.materialUse,
				"operationsSegment": {
					"id": QueryJobOrderDetails.data.data.getJobOrder.operationsSegment.id
				},
				"jobResponse": {
					"id": QueryJobOrderDetails.data.data.getJobOrder.jobResponses[0].id
				},
				"owner": {
					"id": selectOwnerProdNonSerialised.selectedOptionValue
				},
				"quantity": parseFloat(inpProduceQuantity.text)
				}
				return ret
			}
			if (tabsProducePalletBinSelect.selectedTab == "Bin") {
				let ret = {
				"material": {
					"code": GetMaterialSpecification.data.data.getMaterialSpecification.material.code
				},
				"status": {
					"code": GetMaterialSpecification.data.data.getMaterialSpecification.allowedStates[0].code
				},
				"storageLocation": {
					"name": inpProduceBinLabel.text
				},
				"materialUse": GetMaterialSpecification.data.data.getMaterialSpecification.materialUse,
