actionConfiguration.body:
() => {
		var ret = []

		if (QueryJobOrderDetails.data.data != null) {
			var val = QueryJobOrderDetails.data.data.getJobOrder.operationsSegment.materialSpecifications
			for (let i = 0; i < val.length; i++) {
				if (val[i].material.code == GetMaterialSubLot.data.data.getMaterialSubLot.material.code) {
					if (FormatMaterialUnitDropDowns.isOutputMaterialUse(val[i].materialUse)) {
						let line = {
							"label" : val[i].allowedStates[0].code,
							"value" : val[i].allowedStates[0].id
						}
						ret.push(line)
					}
				}

			}
		}
		return ret
	}
