actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			id:selectMaterialProduction.selectedOptionValue
		},
    query: `
query GetMaterialSpecification($id:ID!){
  getMaterialSpecification(id:$id) {
    material{
      id
      code
      inventoryHandlingPolicy {
        trackBySerialNumber
        trackByCarrier
      }
			materialClass {
				code
			}
    }
    allowedStates{id,code}
    materialUse
    operationsSegment{
      id
    }
  }
}
    `
})
}}

