actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			code:InputUnitDetailsBarcode.text,
			warehouse: [selectWarehouse.selectedOptionValue]
		},
    query: `
query QueryMaterialSubLot($code:String!, $warehouse:[ID!]!) {
  getMaterialSubLot(code:$code){
		code
		material {
			code
			materialClass {
				code
			}
			inventoryHandlingPolicy {
				rules @cascade{
					id
					warehouse (filter:{id:$warehouse}){
						id
					}
					stateModel {
						states {
							status {
								code
							}
							to {
								code
							}
						}
					}
				}
			}
		}
		properties {
			id
			name
			description
		}
  }
}
    `
})
}}

