actionConfiguration.body:
{{
JSON.stringify({
    variables: {
  		"equipmentId": [SelectCreateOrderWarehouse.selectedOptionValue],
  		"materialId": [SelectCreateOrderMaterial.selectedOptionValue],
  		"equipmentIdS": [SelectCreateOrderWarehouse.selectedOptionValue]
		},
    query: `
query GetOperationsRequest($equipmentId:[ID!]!, $equipmentIdS:[String!]!,$materialId:[ID!]!){
  queryOperationsRequest @cascade{
    code
		id
    equipment(filter:{id:$equipmentId}) {
      id
    }
    operationsDefinition {
      name
      material (filter:{id:$materialId}) {
        id
        materialEquipment (filter:{equipmentID:$equipmentIdS}){
          planningSummaryByState(filter:{isPlanned:true}) {
            planQty
            stockOnHand
          }
        }
      }
      segments {
        label:name
				value:id
      }
    }
  }
}
    `
})
}}

