actionConfiguration.body:
() => {
		if (GetMaterialSpecification.data.data.getMaterialSpecification.material.inventoryHandlingPolicy.trackBySerialNumber == true) {
			if (GetMaterialSpecification.data.data.getMaterialSpecification.materialUse == "Consumed") {
				return "Consume Serialised"
			}
			return "Produce Serialised"
		} else {
			var use = GetMaterialSpecification.data.data.getMaterialSpecification.materialUse
			if (use == "Consumed") {
				return "Consume Non-serialised"
			} 
			return "Produced Non-serialised"
		}
	}

