actionConfiguration.body:
{{
JSON.stringify({
    variables: {
  "currentJobOrderID": QueryJobOrderDetails.data.data.getJobOrder.id,
  "currentJobResponseID": appsmith.store.currentJobResponseID,
			"endDateTime":moment()
},
    query: `
mutation stopOrder($currentJobResponseID:[ID!]!,$currentJobOrderID:[ID!]!,$endDateTime:DateTime){
  updateJobResponse(input:{filter:{id:$currentJobResponseID},set:{endDateTime:$endDateTime}}){
    jobResponse{
      id
    }
  }
  updateJobOrder(input:{filter:{id:$currentJobOrderID},set:{dispatchStatus:PARKED}}){
    jobOrder{
      id
    }
  }
}
    `
})
}}

