actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			"subLotUpdate": {
    		"filter": {"code": {"eq": InputUnitDetailsBarcode.text}},
    			"set": {"properties": [
      		{
        		"name": inpPropertyName.text,
        		"description": inpPropertyValue.text
     		 	},
    		]}
  		}
		},
    query: `
mutation UpdateMaterialSubLot($subLotUpdate:UpdateMaterialSubLotInput!){
  updateMaterialSubLot (input:$subLotUpdate){
    materialSubLot {
			id
		}
  }
}
    `
})
}}

