actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			id:selectWorkCell.selectedOptionValue
		},
    query: `
query($id:ID!){
	getEquipment(id:$id){
		id
		name
		jobOrders(filter:{dispatchStatus:{in:[ACTIVE,CREATED,PENDING,PARKED]}}){
      name
      dispatchStatus
      operationsSegment{
        name
        operationsDefinition{
          material{
            code
            name
            description
          }
        }
      }
    }
	}
}
    `
})
}}

