actionConfiguration.body:
{{
JSON.stringify({
	variables: {
			id:QueryJobOrderDetails.data.data.getJobOrder.jobResponses[0].id,
			code:QueryJobOrderDetails.data.data.getJobOrder.operationsRequest.operationsDefinition.material.code
		},
    query: `
query GetStockOnHand($id:ID!,$code:String!){
  getStockOnHand(filter:{jobResponse:{id:$id},material:{code:$code},groupBy:{subLot:true,carrier:true,status:true},minimumStock:1}) {

        materialSubLot {
      		code
					id
      		properties {
        		name
        		description
      		}
    		}
				carrier {
					code
				}
				status {
					code
				}
    		quantity
  		}
}
    `
})
}}

