actionConfiguration.body:
{{
JSON.stringify({
	variables: {
			id:ListOrders.selectedItem.jobResponses[0].id
		},
    query: `
query GetStockOnHand($id:ID!){
  getStockOnHand(filter:{jobResponse:{id:$id},groupBy:{subLot:true},minimumStock:1}) {

        materialSubLot {
      		code
					id
      		properties {
        		name
        		description
      		}
    		}
				carrier {
					code
				}
				status {
					code
				}
    		quantity
  		}
}
    `
})
}}

