		},
    query: `
query GetStockOnHand($code:String!){
  getStockOnHand(filter:{materialSubLot:{code:$code},groupBy:{status:true}}){
    detail {
      stock {
        status{
          code
          id
        }
      }
    }
  }
}
    `
})
}}

