actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			code:InputUnitDetailsBarcode.text,
			warehouse: [selectWarehouse.selectedOptionValue]
		},
    query: `
query QueryMaterialSubLot($code:String!, $warehouse:[ID!]!) {
  getMaterialSubLot(code:$code){
		code
		material {
			code
			materialClass {
				code
			}
			operationsDefinitions {
        segments {
          id
					equipmentSpecifications @cascade{
            equipment (filter:{id:$warehouse}){
              id
            }
          }
          materialSpecifications {
            id
            materialUse
            material{
              code
            }
            allowedStates {
              id
              code
            }
          }
        }
      }
		}
		properties {
			id
			name
			description
		}
  }
}
    `
})
}}

