actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			"addInventory": [
  			{
    			"materialSubLot": {
      			"code": GetMaterialSubLot.data.data.getMaterialSubLot.code
    			},
					"materialDefinition": {
						"code": GetMaterialSubLot.data.data.getMaterialSubLot.material.code,
						"id": GetMaterialSubLot.data.data.getMaterialSubLot.material.id
					},
    			"jobResponse": {
      			"id": QueryJobOrderDetails.data.data.getJobOrder.jobResponses[0].id
    			},
    			"carrier": {
      			"id": QueryCarrier.data.data.getCarrier.id
    			},
    			"materialUse": "Produced",
    			"materialStatus": {
     			 "id": treeSerialStatusSelection.selectedOptionValue
    			},
					"user": {
						"id": GetUserSites.data.data.getUser.id
					}
  			}
			],
		},
    query: `
mutation AddInventoryActual($addInventory:[AddInventoryActualInput!]!){
  addInventoryActual(input:$addInventory){
    quantity
  }
}
    `
})
}}

