{{
JSON.stringify({
	variables: {
			warehouse:selectWarehouse.selectedOptionValue
		},
    query: `
query QueryJobOrdersWarehouse($warehouse:ID!) {
  getEquipment(id:$warehouse){
    jobOrders{
      id
    name
    dispatchStatus
    quantity
    quantityUoM{
      code
    }
    operationsRequest{
			id
			code
      quantity
			quantityUoM{
				code
			}
      operationsDefinition{
        material{
          code
        }
      }
		}
    }
  }
}
    `
})
}}

